import React from 'react';
import './Information.css'
const Solution = () => {
    return(
        <div className='content'>
            <h3>
            Now more than ever, it's vital for older adults to safeguard against falls. 
            From regular exercise to vision checks and home safety upgrades, 
            every precaution counts. Healthcare providers must prioritize assessing 
            fall risk in elderly patients, offering tailored interventions to 
            prevent falls and related injuries. 
            </h3>
            <h1>And with Fall Guys, detecting falls becomes easier than ever, ensuring timely support for those who need it most.

            </h1>







            
        </div>
    );
};
export default Solution;
